CREATE TABLE dbo.Season
(
	seasonId INT IDENTITY PRIMARY KEY,
	season_name VARCHAR(6) NULL,

)

CREATE TABLE dbo.Genre
(
	genreId INT IDENTITY NOT NULL
		CONSTRAINT PK_Genre_genreId PRIMARY KEY,
	genre VARCHAR(50) NOT NULL,
)

CREATE TABLE dbo.Titles
(
	titleId INT IDENTITY NOT NULL
		CONSTRAINT PK_Titles_titleId PRIMARY KEY,
	seasonId INT NOT NULL
		CONSTRAINT FK_Titles_Season_seasonId
			FOREIGN KEY REFERENCES dbo.Season(seasonId),
	picture VARCHAR(MAX),
	title VARCHAR(200),


)

CREATE TABLE dbo.Studio
(
	studioId INT IDENTITY NOT NULL
		CONSTRAINT PK_Studio_studioId PRIMARY KEY,
	titleId INT NOT NULL 
		CONSTRAINT FK_Studios_Titles_titleId
			FOREIGN KEY REFERENCES dbo.Titles(titleId),
	Name VARCHAR(100) NOT NULL
)

CREATE TABLE dbo.GTLink
(
	genreId INT NOT NULL
		CONSTRAINT FK_GTLink_Genre_genreId
			REFERENCES dbo.Genre(genreId),
    titleId INT NOT NULL
		CONSTRAINT FK_GTLink_Titles_titleId
			REFERENCES dbo.Titles(titleId),
    PRIMARY KEY (genreId, titleId)
)


